import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Home = () => {
  const [username, setUsername] = useState('');
  const [showMenu, setShowMenu] = useState(false);
  const [showDropdown, setShowDropdown] = useState(false);
  const [selectedName, setSelectedName] = useState(null);

  // Only one option in the list now:
  const [list] = useState([
    { Name: 'Requesting for Manpower', stateName: 'MANPOWER' },
  ]);

  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      const user = JSON.parse(storedUser);
      setUsername(user.user_id);
    } else {
      navigate('/');
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/');
  };

  const handleChangePassword = () => {
    navigate('/change-password');
  };

  const handleSelectChange = (event) => {
    const selected = list.find(item => item.stateName === event.target.value);
    setSelectedName(selected);
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <div className="bg-[#2e5d95] text-white flex justify-between items-center px-6 py-3 shadow relative">
        <Link to="/">
          <div className="text-lg font-semibold">Home</div>
        </Link>
        <div className="text-xl font-bold">Head of Division</div>

        <div className="relative">
          <div
            className="text-sm font-semibold cursor-pointer"
            onClick={() => setShowMenu(!showMenu)}
          >
            {username} â–¾
          </div>
          {showMenu && (
            <div className="absolute right-0 mt-2 w-48 bg-white text-black shadow-md rounded z-10">
              <button
                onClick={handleChangePassword}
                className="block w-full px-4 py-2 text-left hover:bg-gray-100"
              >
                Change Password
              </button>
              <button
                onClick={handleLogout}
                className="block w-full px-4 py-2 text-left hover:bg-gray-100"
              >
                Logout
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Body */}
      <div className="p-6">
        {/* Top Button */}
        <div className="flex justify-start gap-4 mb-6">
          <button
            className="bg-[#2e5d95] text-white px-4 py-2 font-semibold rounded shadow hover:bg-blue-800"
            onClick={() => setShowDropdown(!showDropdown)}
          >
            Create Project Request
          </button>
        </div>

        {/* Dropdown + Manpower Form */}
        {showDropdown && (
          <div className="container bg-white p-4 rounded shadow-md">
            <div className="mb-4 flex items-center gap-2">
              <label className="text-lg font-medium" htmlFor="serviceSelect">Select :</label>
              <select
                id="serviceSelect"
                className="border px-3 py-1 rounded"
                value={selectedName?.stateName || ''}
                onChange={handleSelectChange}
              >
                <option value="" disabled>
                  -- Select Service --
                </option>
                {list.map((li) => (
                  <option key={li.stateName} value={li.stateName}>
                    {li.Name}
                  </option>
                ))}
              </select>
            </div>

            {/* Since only one option, directly show form if selected */}
            {selectedName && selectedName.stateName === 'MANPOWER' && (
              <div className="mt-6">
                <form
                  name="manpowerRequestForm"
                  onSubmit={(e) => {
                    e.preventDefault();
                    alert('Manpower form submitted!');
                  }}
                >
                  <h4 className="font-bold text-xl mb-4">
                    Requisition for Extraneous Manpower
                  </h4>

                  <div className="grid grid-cols-4 gap-4 mb-4">
                    <div>
                      <label htmlFor="division" className="block mb-1 font-medium">Select Division</label>
                      <select
                        id="division"
                        className="w-full border rounded p-2"
                        required
                      >
                        <option value="">Select Division</option>
                        <option value="div1">Division 1</option>
                        <option value="div2">Division 2</option>
                      </select>
                    </div>

                    <div>
                      <label htmlFor="type" className="block mb-1 font-medium">Select Type</label>
                      <select
                        id="type"
                        className="w-full border rounded p-2"
                        required
                      >
                        <option value="">Select Type</option>
                        <option value="type1">Type 1</option>
                        <option value="type2">Type 2</option>
                      </select>
                    </div>

                    <div>
                      <label htmlFor="commencement" className="block mb-1 font-medium">Date Of Commencement</label>
                      <input
                        type="date"
                        id="commencement"
                        className="w-full border rounded p-2"
                        required
                      />
                    </div>

                    <div>
                      <label htmlFor="completion" className="block mb-1 font-medium">Expected Date Of Completion</label>
                      <input
                        type="date"
                        id="completion"
                        className="w-full border rounded p-2"
                        required
                      />
                    </div>
                  </div>

                  <div className="grid grid-cols-4 gap-4 mb-4">
                    <div>
                      <label htmlFor="nature" className="block mb-1 font-medium">Nature Of Work/Job</label>
                      <input
                        type="text"
                        id="nature"
                        className="w-full border rounded p-2"
                        required
                      />
                    </div>

                    <div>
                      <label htmlFor="project" className="block mb-1 font-medium">Name Of The Project</label>
                      <input
                        type="text"
                        id="project"
                        className="w-full border rounded p-2"
                        required
                      />
                    </div>

                    <div>
                      <label htmlFor="workorder" className="block mb-1 font-medium">Work Order No.</label>
                      <input
                        type="text"
                        id="workorder"
                        className="w-full border rounded p-2"
                        required
                      />
                    </div>

                    <div>
                      <label htmlFor="valueorder" className="block mb-1 font-medium">Value Of The Work Order</label>
                      <input
                        type="text"
                        id="valueorder"
                        className="w-full border rounded p-2"
                        required
                      />
                    </div>
                  </div>

                  <div className="flex justify-center gap-4 mt-6">
                    <button
                      type="submit"
                      className="bg-[#2e5d95] text-white px-6 py-2 rounded hover:bg-blue-800 font-semibold"
                    >
                      Save
                    </button>
                    <button
                      type="button"
                      className="bg-gray-400 text-white px-6 py-2 rounded hover:bg-gray-600 font-semibold"
                      onClick={() => {
                        setSelectedName(null);
                      }}
                    >
                      Cancel
                    </button>
                  </div>
                </form>
              </div>
            )}
          </div>
        )}

        {/* Divider */}
        <hr className="border-dashed border-t-2 border-[#2e5d95] my-6" />

        {/* Center Button */}
        <div className="flex justify-center">
          <button className="bg-[#2e5d95] text-white px-4 py-2 font-semibold rounded shadow hover:bg-blue-800">
            Requested Service
          </button>
        </div>
      </div>
    </div>
  );
};

export default Home;
