import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
   const [username, setUsername] = useState('');

useEffect(() => {
  const storedUser = localStorage.getItem('user');
  if (storedUser) {
    const user = JSON.parse(storedUser);
    setUsername(user.name); // ðŸ‘ˆ Use the name field here
  }
}, []);

 
  return (
    <div className="min-h-screen bg-gray-100">
     
      <div className="bg-[#2e5d95] text-white flex justify-between items-center px-6 py-3 shadow">
        <Link to="/">
          <div className="text-lg font-semibold">Home</div>
        </Link>
        <div className="text-xl font-bold">Head of Division</div>
        <div className="text-sm font-semibold">{username} </div> 
      </div> 

      {/* Body Content */}
      <div className="p-6">
        {/* Top Buttons */}
        <div className="flex justify-start gap-4 mb-6">
          <button className="bg-[#2e5d95] text-white px-4 py-2 font-semibold rounded shadow hover:bg-blue-800">
            Create Project Request
          </button>
        </div>

        {/* Dashed Divider */}
        <hr className="border-dashed border-t-2 border-[#2e5d95] mb-6" />

        {/* Center Button */}
        <div className="flex justify-center">
          <button className="bg-[#2e5d95] text-white px-4 py-2 font-semibold rounded shadow hover:bg-blue-800">
            Requested Service
          </button>
        </div>
      </div>
    </div>
  );
};

export default Home;
